import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Withdraw = () => {
    const [withdraw, setWithdraw] = useState(0);
    const [balance, setBalance] = useState(0);
    const [value, setValue] = useState({
        datetime: "",
        action: 'withdraw',
        amount: 0,
        remain: 0
    });
    const navigate = useNavigate();

    useEffect (() => {
        getBalance();
    })

    const getBalance = () => {
        axios.get('https://localhost:7186/api/Users')
            .then(result => {
                if (result.data && result.data.length > 0) {
                    setBalance(result.data[0].balance);
                    setValue(prevValue => ({
                        ...prevValue,
                        amount: 0,
                        remain: result.data[0].balance,
                    }));
                }
            }).catch(error => {
                console.error('Error fetching balance:', error);
            });
    }


    const handleSubmit = () => {
        axios.post('https://localhost:7186/api/Transaction', value)
        .then(axios.put('https://localhost:7186/api/Users' + user_id))
    }

    const handleWithdraw = (value) => {
        setWithdraw(value)
    }

    return (
        <div>
            <div>
                <h1>Withdraw</h1>
                <form className='border w-50 p-3 ' style={{ justifyContent: 'center', textAlign: 'center', margin: 'auto' }} onSubmit={handleSubmit}>
                    <div class="mb-3 mt-3 ">
                        <input type="text" class="form-control" id="Withdraw" placeholder="Enter Money" onChange={(e) => handleWithdraw(e.target.value)} name="Money" />
                    </div>
                    <div>
                        <button className='btn btn-outline-success'>Confirm</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Withdraw
