import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
const History = () => {
    
  const [transfer, setTransfer] = useState([]);
  const [receive, setReceive] = useState([]);
  const [userId, setUserId] = useState(0);
  const [transactions, setTransactions] = useState([]);

    useEffect(() => {
        const fetchTransactions = async () => {
          const token = localStorage.getItem('token');

            if (token) {
                const decodedToken = jwtDecode(token);
                const user_id = decodedToken.unique_name;
                setUserId(user_id);}
          try {
            const response = await axios.get(`https://localhost:7186/api/user/${userId}`);
            setTransactions(response.data);
          } catch (error) {
            console.error('Error fetching transactions:', error);
          }
        };
    
        fetchTransactions();
      }, [user_id]);

      

      const formatDatetime = (datetime) => {
        if (new Date(datetime).toISOString() === '0001-01-01T00:00:00') {
          return 'Not Available';
        }

        return datetime;
      };

      return (
        <div>
          <div>
            <div>
              <h1>History</h1>
            </div>
            <div>
              <button className='btn btn-outline-dark'>Withdraw</button>
            </div>
            <div>
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">Date time</th>
                    <th scope="col">Action</th>
                    <th scope="col">Remain</th>
                    <th scope="col">Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((transaction, index) => (
                    <tr key={index}>
                      <th scope="row">{index + 1}</th>
                      <td>{formatDatetime(transaction.date_time)}</td>
                      <td>{transaction.action}</td>
                      <td>{transaction.remain}</td>
                      <td>{transaction.amount}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      );
    };
    
    export default History;
