import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Transfer = () => {
    const [deposit, setDeposit] = useState(0);
    const [balance, setBalance] = useState(0);
    const [userId, setUserId] = useState();
    const [transfer, setTransfer] = useState({
        datetime: '',
        action: '',
        amount: 0,
        remain: 0,
        from: '',
        user_id: 0,
    });

    const navigate = useNavigate();

    useEffect(() => {
        const fetchBalance = async () => {
            const token = localStorage.getItem('token');

            if (token) {
                const decodedToken = jwtDecode(token);
                const user_id = decodedToken.unique_name;
                setUserId(user_id);

                try {
                    const result = await axios.get(`https://localhost:7186/api/Users/`);
                    if (result.data && result.data.length > 0) {
                        setBalance(result.data[0].balance);
                        setValue((prevValue) => ({
                            ...prevValue,
                            amount: 0,
                            remain: result.data[0].balance,
                        }));
                    }
                } catch (error) {
                    console.error('Error fetching balance:', error);
                }
            } else {
                console.error('JWT token not found in local storage');
            }
        };

        fetchBalance();
    }, [userId]);

    const handleSubmit = () => {
        navigate('/');
    }


    return (
        <div>
            <div>
                <h1>Transfer</h1>
                <form className='border w-50 p-3 ' style={{ justifyContent: 'center', textAlign: 'center', margin: 'auto' }} onSubmit={handleSubmit}>
                    <div class="mb-3 mt-3 ">
                        <input type="text" class="form-control" id="Transfer" placeholder="Enter Money" name="Money" />
                    </div>
                    <h1>To</h1>
                    <div class="mb-3 mt-3 ">
                        <input type="text" class="form-control" id="user" placeholder="User" name="user" />
                    </div>
                    <div>
                        <button className='btn btn-outline-success'>Confirm</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Transfer
