import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Transfer = () => {
    const [transfer, setTransfer] = useState(0);
    const [balance, setBalance] = useState(0);
    const [userId, setUserId] = useState();
    const [user, setUser] = useState('');
    const [toUser, setToUser] = useState('');
    const [value, setValue] = useState({
        date_time: '',
        actions: 'transfer',
        amount: 0,
        remain: 0,
        from_user: '',
        user: '', 
    });
    const [receive, setReceive] = useState({
        date_time: '',
        actions: 'receive',
        amount: 0,
        remain: 0,
        from_user: '',
        user: '', 
    })

    const navigate = useNavigate();

    useEffect(() => {
        const fetchBalance = async () => {
            const token = localStorage.getItem('token');

            if (token) {
                const decodedToken = jwtDecode(token);
                const user_id = decodedToken.unique_name;
                setUserId(user_id);

                try {
                    const result = await axios.get(`https://localhost:7186/api/Users/`);
                    if (result.data && result.data.length > 0) {
                        setBalance(result.data[0].balance);
                        setUser(result.data[0].name); 
                    }
                } catch (error) {
                    console.error('Error fetching balance:', error);
                }
            } else {
                console.error('JWT token not found in local storage');
            }
        };

        fetchBalance();
    }, [userId]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (transfer <= 0) {
            alert('Amount must be greater than zero.');
            return;
        }
        console.log(user);
        const currentTime = new Date().toISOString();
        const balanceInt = parseInt(balance);
        const transferInt = parseInt(transfer);
        const newBalance = balanceInt + transferInt;

        setValue((prevValue) => ({
            ...prevValue,
            date_time: currentTime,
            amount: parseInt(transfer),
            remain: newBalance,
            from_user: toUser,
            user: user
        }));

        setReceive((prevValue) => ({
            ...prevValue,
            date_time: currentTime,
            amount: parseInt(transfer),
            remain: newBalance,
            from_user: user,
            user: toUser
        }));

        

        try {
            await axios.post('https://localhost:7186/api/Transfers', value);
            await axios.post('https://localhost:7186/api/Transfers', receive);
            await axios.put(`https://localhost:7186/api/Users/${userId}`, {
                balance: newBalance,
            });
            console.log('User balance updated successfully.');
            console.log('New Balance:', newBalance);
            alert('Transfer successful.');
            navigate('/home');
        } catch (error) {
            console.log(value)
            console.error('Error during transfer:', error);
            alert('Transfer failed. Please try again.');
        }
    };

    const handleTransfer = (value) => {
        setTransfer(value);
    }

    const getUser = (value) => {
        setToUser(value);
    }

    return (
        <div>
            <div>
                <h1>Transfer</h1>
                <form className='border w-50 p-3 ' style={{ justifyContent: 'center', textAlign: 'center', margin: 'auto' }} onSubmit={handleSubmit}>
                    <div className="mb-3 mt-3">
                        <input type="text" className="form-control" id="Transfer" placeholder="Enter Money" onChange={(e) => handleTransfer(e.target.value)} name="Money" />
                    </div>
                    <h1>To</h1>
                    <div className="mb-3 mt-3">
                        <input type="text" className="form-control" id="user" placeholder="User" onChange={(e) => getUser(e.target.value)} name="user" />
                    </div>
                    <div>
                        <button className='btn btn-outline-success'>Confirm</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Transfer;
