import React, { useEffect, useState } from 'react'
import './style.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Home = () => {

const [balance, setBalance] = useState();

useEffect(() => {
    getBalance();
}, [])

const getBalance = () => {
    axios.get('https://localhost:7186/api/Users')
        .then(result => {
            if (result.data && result.data.length > 0) {
                setBalance(result.data[0].balance);
                console.log(result.data);
            }
            console.log(balance);
        })
        .catch(error => {
            console.error('Error fetching balance:', error);
        });
}

const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
}

    return (
        <div className='container'>
            <div className='m-5 p-5 border' style={{ width: '32%'}}>
                <div className='balance'>
                    <div className='balance_text'>
                        <h3>Balance</h3>
                        <h1>{balance}</h1>
                    </div>
                </div>
                <div>
                    <Link to='/withdraw' className='btn btn-outline-dark btn-withdraw'>Withdraw</Link>
                    <Link to='/transfer' className='btn btn-outline-dark btn-transfer'>Transfer</Link>
                    <Link to='/deposit' className='btn btn-outline-dark btn-deposit'>Deposit</Link>
                </div>
                <div>
                    <Link to='/history' className='btn btn-outline-dark btn-history'>History</Link>
                    <Link onClick={handleLogout} className='btn btn-outline-alert btn-logout'>Logout</Link>
                </div>
            </div>
        </div>
    )
}

export default Home
